import logo from './logo.svg';
import './App.css';
function getFile(event){
        event.preventDefault();
        if(event.dataTransfer.items){
            var file=event.dataTransfer.items[0].getAsFile();
            let reader = new FileReader();
            reader.readAsText(file);
            reader.onload = function() {
                console.log(reader.result);
                document.getElementById('result').textContent=reader.result;
            };
        }
  }
  function preventOpen(event) {
    event.preventDefault();
  }
  function getURL(){
    var xhrequest = new XMLHttpRequest();
        xhrequest.open("GET", document.getElementById("url").value, true);
        xhrequest.send(null);
        xhrequest.onreadystatechange = function () {
            if (xhrequest.readyState === 4 && xhrequest.status === 200) {
                res=xhrequest.responseText;
                if(res!=""){
                  document.getElementById('result').textContent=res;
                }
                else{
                  document.getElementById('result').textContent="Kein Text ausgew채hlt";
                }
            }
        }
  }
  function getText(){
      var res=document.getElementById('textc').value;
      if(res!=""){
        document.getElementById('result').textContent=document.getElementById('textc').value;
      }
      else{
        document.getElementById('result').textContent="Kein Text ausgew채hlt";
      }
  }
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and do not save to reload.
        </p>
          Learn React
      </header>
    </div>
  );
}

export default App;
